// Implemented an audio service to provide sound feedback for game events.

// A user interaction is required to initialize the AudioContext.
// We will call init() from a component when the app starts.
let audioContext: AudioContext | null = null;

// Base64 encoded audio for embedding directly, avoiding extra file requests.
// Source: Tiny, well-compressed sounds from free sound effect libraries.

// A short, positive chime for completing a mission
const MISSION_COMPLETE_SOUND = 'data:audio/wav;base64,';

// An uplifting, slightly longer sound for leveling up
const LEVEL_UP_SOUND = 'data:audio/wav;base64,';

// A serene, conclusive sound for completing all daily missions
const ALL_MISSIONS_COMPLETE_SOUND = 'data:audio/wav;base64,';

const soundCache: { [key: string]: AudioBuffer } = {};

const initAudio = () => {
  if (!audioContext) {
    audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
  }
};

const playSound = async (soundUrl: string, volume: number = 0.5) => {
  if (!audioContext) {
    console.warn("AudioContext not initialized. Call initAudio() on a user interaction.");
    return;
  }
  // Resume context if it was suspended
  if (audioContext.state === 'suspended') {
      await audioContext.resume();
  }

  try {
    let buffer;
    if (soundCache[soundUrl]) {
      buffer = soundCache[soundUrl];
    } else {
      const response = await fetch(soundUrl);
      const arrayBuffer = await response.arrayBuffer();
      buffer = await audioContext.decodeAudioData(arrayBuffer);
      soundCache[soundUrl] = buffer;
    }

    const source = audioContext.createBufferSource();
    const gainNode = audioContext.createGain();

    source.buffer = buffer;
    gainNode.gain.setValueAtTime(volume, audioContext.currentTime);

    source.connect(gainNode);
    gainNode.connect(audioContext.destination);
    
    source.start(0);
  } catch (error) {
    console.error("Error playing sound:", error);
  }
};

export const audioService = {
  init: initAudio,
  playMissionComplete: () => playSound(MISSION_COMPLETE_SOUND, 0.3),
  playLevelUp: () => playSound(LEVEL_UP_SOUND, 0.5),
  playAllMissionsComplete: () => playSound(ALL_MISSIONS_COMPLETE_SOUND, 0.6),
};